mapper.select:
SELECT balance
from card
WHERE card.cardID = #{userID}

mapper.insert:
insert into transrecord(money, type, username)
values (#{transactionMoney}, #{transactionType}, #{userID});


update card
set balance = #{transactionMoney}
where cardID = #{userID}

update card
set balance = balance - #{money}
where cardID = #{userID};

